      album_art: search_albums_table.selectedRow.cover_image,
      favourite: false
    }).then(() => {
      search_tracks_table.tableData.forEach((element, index) => {
        insert_track.run(() => {
          showAlert('Album [' + search_albums_table.selectedRow.title + '] added', 'success');
        }, () => {
          showAlert('Insert Track Failed: ' + insert_track.data.match(/.*/)[0], 'error');
