          album_id: insert_album.data.map(row => row.insert_album)[0].album_id,
          track_number: index + 1,
          track: element.title.replace('\'', '\'\''),
          duration: element.duration.concat(':'),
          play: [{
            "play_url": "",
            "play_name": ""
          }],
          favourite: false,
          collection_id: appsmith.store.collection_id
        }).then(() => showAlert('Album [' + search_albums_table.selectedRow.title + '] added', 'success')).catch(() => showAlert('Insert Track Failed: ' + insert_track.data.match(/.*/)[0], 'error'));
