actionConfiguration.body:
(collection_id, album_id) => {
  showAlert('Loading tracks...').then(() => storeValue('response', undefined)).then(() => storeValue('query_input', 'SELECT get_tracks_mobile(p_collection_id =>' + collection_id + ',p_album_id =>' + album_id + ',p_favourites_only =>' + favourites_switch.isSwitchedOn + ')')).then(() => query_api.run()).then(() => {
    if (JSON.parse(query_api.data).data.length == 0) {
      storeValue('level', 'album');
      showAlert('No tracks found', 'warning');
    } else storeValue('response', JSON.parse(query_api.data).data.map(row => row.get_tracks_mobile));
  });
}

