actionConfiguration.body:
() => {
  switch (appsmith.store.level) {
    case 'artist':
      storeValue('object_id', dynamic_table.selectedRow.artist_id).then(() => toggle_favourite.run()).then(() => JS_global_mobile.query_artists(appsmith.store.collection_id));
      break;
    case 'album':
      storeValue('object_id', dynamic_table.selectedRow.album_id).then(toggle_favourite.run()).then(() => JS_global_mobile.query_albums(appsmith.store.collection_id, dynamic_table.selectedRow.artist_id));
      break;
    case 'track':
      storeValue('object_id', dynamic_table.selectedRow.track_id).then(toggle_favourite.run()).then(() => JS_global_mobile.query_tracks(appsmith.store.collection_id, dynamic_table.selectedRow.album_id));
      break;
  }
}

